import "./MenuComponent.css";
import { Row,Col,
  Navbar,
  Collapse,
  NavbarToggler,
  Nav,
  NavItem,
  NavLink
  } from "reactstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from "react"; 



export default function MenuComponent(){
  var listMenuOfMen = {
    listMenu: ["wallet","shoes","bags","accessories"]
  }

  var listMenuOfWoMen = {
    listMenu: ["shirt","trousers","bags","accessories"], 
  }




  const [isOpen, setIsOpen] = useState(false);

  const toggle1 = () => setIsOpen(!isOpen);


  const [dropdownOpen, setOpen] = useState({
    isDropdown:false
  });

  var dropdownList ;
  if(dropdownOpen.isDropdown){
    dropdownList = "dropdown-item" ;
  }else{
    dropdownList = "dropdown-hidden";
  }
   return(
     <Row className="menu">
       <Col lg="12" sm="10">
         <Row className="top-menu">
           <Col lg="11" sm="6" xs="6">
              <h3 class="logo-nav">LEONARDO</h3>
           </Col>
           <Col lg="1" sm="6" xs="6">
           <div className="sidebar-menu">
              <input type="button" class="btn btn-default" value="Searching"/>
              <input type="button" class="btn btn-default" value="Cart" />
            </div>
           </Col>      
        </Row>
       </Col>
       <Col lg="12" sm="2">
       <Navbar light expand="md" className="m-0 p-0 menu-dropdown">
       <NavbarToggler onClick={toggle1} />
          <Collapse isOpen={isOpen} navbar>
              <Nav className="mr-auto" className="menu-bar">
                <NavItem className="animation-link" >
                  <NavLink href="/components/">NEW ARRIVALS</NavLink>
                </NavItem>

                <div className="p-0 m-0" onMouseOut={() => { setOpen({ isDropdown:false }); }} onMouseOver={() => { setOpen({ isDropdown:true }); }}>
                  <NavItem className="animation-link"> 
                        <NavLink href="/components/">MEN</NavLink> 
                  </NavItem>
                    <div className={dropdownList} >
                      <div className="item-menu">
                        <div>ABC</div>
                        <div>BCD</div>
                      </div>
                    </div>
                </div>

                <div className="p-0 m-0" onMouseOut={() => { setOpen({ isDropdown:false }); }} onMouseOver={() => { setOpen({ isDropdown:true }); }}>
                  <NavItem className="animation-link"> 
                        <NavLink href="/components/">WOMEN</NavLink> 
                  </NavItem>
                    <div className={dropdownList} >
                      <div className="item-menu">
                        <div>BCD</div>
                        <div>ITF</div>
                      </div>
                    </div>
                </div>

                <NavItem className="animation-link">
                  <NavLink href="#">BLOG</NavLink>
                </NavItem>        
              </Nav>
          </Collapse>
          </Navbar>
       </Col>
     </Row>
   );
}
