import React, { useState } from 'react';
import {
    Row,
    Col,
    Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button
} from 'reactstrap';

import "./ItemComponent.css"

import img from "../img/img"


import 'bootstrap/dist/css/bootstrap.min.css';
export default function ItemComponent(){
    const listItemFashion = {
        bag: [
            {
                imgDisplay: img[0],
                imgContinue: img[1],
                name: "Túi Messenger Ipad Camo",
                price: "460.000₫",
                typeColor:[]    
            },
            {
                imgDisplay: img[2],
                imgContinue: img[3],
                name: "Túi Mini Camo",
                price: "290.000₫",
                typeColor:[]    
            } ,  
            {
                imgDisplay: img[4],
                imgContinue: img[5],
                name: "Túi Đeo Chéo Zumba Camo",
                price: "250.000₫",
                typeColor:[]    
            } ,  
            {
                imgDisplay: img[6],
                imgContinue: img[7],
                name: "Túi Grand Camo",
                price: "360.000₫",
                typeColor:[]    
            }  ,
            {
                imgDisplay: img[8],
                imgContinue: img[9],
                name: "Túi Crossbody Camo",
                price: "395.000₫",
                typeColor:[]    
            }  ,
            {
                imgDisplay: img[10],
                imgContinue: img[11],
                name: "Balo quai chéo Zumba",
                price: "380.000₫",
                typeColor:[]    
            }  ,
            {
                imgDisplay: img[12],
                imgContinue: img[13],
                name: "Túi Hộp Oxford",
                price: "320.000₫",
                typeColor:[{
                    color:"gray",
                    imgColor:img[12]
                },
                {
                    color:"black",
                    imgColor:img[14]
                }]    
            }  ,
            {
                imgDisplay: img[15],
                imgContinue: img[16],
                name: "Túi Đeo Chéo Lớn Zumba",
                price: "340.000₫",
                typeColor:[]    
            }  

        ]
    }

    const[Slide, setSlide] = useState({
        isSlide:false
    })

    var slideStyle = "btn btn-default ";

    slideStyle = Slide.isSlide ? (slideStyle + "slide-style") : (slideStyle + "see-all-style");

    const[isChangeImg, setChangeImg] = useState({
        isChange: "",
        indexActive: -1
    })

    const[isClickChange, setClickChange] = useState({
        isClick: true,
        isIndexActive : 0
    });

    const[isDropdownItem, setDropdownItem] = useState({
        isDropdownItem1: true,
        isDropdownItem2: false
    })

    var classShow1 = (isDropdownItem.isDropdownItem1) ? "wrapper-list-item" : "display-hidden";  
    var classShow2 = (isDropdownItem.isDropdownItem2) ? "wrapper-list-item" : "display-hidden"; 
    function removeId(){
        setTimeout(() => {
            if(!isDropdownItem.isDropdownItem1){
                classShow1 += " disable-show";
            }else if(!isDropdownItem.isDropdownItem2){
                classShow2 += " disable-show";
            }

            if(isDropdownItem.isDropdownItem1){
                classShow1 = classShow1.split(" ")[0];
            }
            if(isDropdownItem.isDropdownItem2){
                classShow2 = classShow2.split(" ")[0];
            }
            console.log(classShow1 , classShow2);
        },1000);
    }



    console.log(classShow1 + "/" + classShow2)
    return(
        <Row>
            <Col>
                <section className="section-spacing">
                    <div className="title-list-item">
                        <Row>
                            <Col xl="12">
                                <div className="small-title">Collection</div>
                            </Col>
                            <Col xl="12">
                                <div className="main-title">
                                        <button className="btn btn-default" onClick={() => {
                                            setDropdownItem({isDropdownItem1:true,isDropdownItem2:false});  
                                            removeId();         
                                            }}>Bags/Briefcases</button>
                                        <button className="btn btn-default" onClick={() =>{
                                            setDropdownItem({isDropdownItem1:false,isDropdownItem2:true});
                                            removeId();    
                                        }}> Card Wallet</button>
                                </div>
                            </Col>
                        </Row>
                    </div>
                    <div className={classShow1} id="list-1">
                        <Row>
                            {
                                listItemFashion.bag.map((item,index) => {
                                    return(
                                        <Col lg="3" sm="6" className="card-spacing">
                                            <Card className="card-style">
                                                {
                                                    (isClickChange.isClick && item.typeColor.length > 0) ? (<CardImg top width="100%" src={item.typeColor[isClickChange.isIndexActive].imgColor} onMouseOver={() => setChangeImg({isChange: item.imgContinue,indexActive:index})} onMouseOut={() => setChangeImg({isChange: item.imgDisplay,indexActive:index})} />)
                                                    :(
                                                    (isChangeImg.indexActive === index) ? (
                                                        <CardImg top width="100%" src={isChangeImg.isChange} onMouseOver={() => setChangeImg({isChange: item.imgContinue,indexActive:index})} onMouseOut={() => setChangeImg({isChange: item.imgDisplay,indexActive:index})} />
                                                     ) : ( 
                                                         (item.typeColor.length > 0) ? (<CardImg top width="100%" src={item.typeColor[isClickChange.isIndexActive].imgColor} onMouseOver={() => setChangeImg({isChange: item.imgContinue,indexActive:index})} onMouseOut={() => setChangeImg({isChange: item.imgDisplay,indexActive:index})} />):
                                                        (<CardImg top width="100%" src={item.imgDisplay} onMouseOver={() => setChangeImg({isChange: item.imgContinue,indexActive:index})} onMouseOut={() => setChangeImg({isChange: item.imgDisplay,indexActive:index})} />)
                                                     )
                                                    )

                                                }

                                                <CardBody className="title-price-style"> 
                                                    <CardTitle tag="h6">{item.name}</CardTitle>

                                                    <CardSubtitle tag="h6" className="mb-2 text-muted">{item.price}</CardSubtitle>

                                                    {
                                                        item.typeColor.length !== 0 ? (
                                                        <div className="btn-change-color"> 
                                                            {
                                                                item.typeColor.map((itemColor,index) => {
                                                                    return (
                                                                    <div className="button-group">  
                                                                    {
                                                                        (itemColor.imgColor === item.imgDisplay) ? (<input type="radio" name="radio" id={index} checked/>) : (<input type="radio" name="radio" id={index}/>)
                                                                    }                                                                   
                                                                        <label className={itemColor.color} title={itemColor.color} onClick={() => setClickChange({isClick : isClickChange.isClick, isIndexActive: index})} for={index}></label> 
                                                                    </div>
                                                                    )
                                                                })
                                                            }
                                                        </div>
                                                        )  :  (<div></div>)
                                                    }    
                                                </CardBody>
                                            </Card>
                                        </Col>
                                    );
                                })
                            }
                        </Row>
                    </div>
                    <div className={classShow2} id="list-2">
                        <Row>
                            {
                                listItemFashion.bag.map((item,index) => {
                                    return(
                                        <Col lg="3" sm="6" className="card-spacing">
                                            <Card className="card-style">
                                                {
                                                    (isClickChange.isClick && item.typeColor.length > 0) ? (<CardImg top width="100%" src={item.typeColor[isClickChange.isIndexActive].imgColor} onMouseOver={() => setChangeImg({isChange: item.imgContinue,indexActive:index})} onMouseOut={() => setChangeImg({isChange: item.imgDisplay,indexActive:index})} />)
                                                    :(
                                                    (isChangeImg.indexActive === index) ? (
                                                        <CardImg top width="100%" src={isChangeImg.isChange} onMouseOver={() => setChangeImg({isChange: item.imgContinue,indexActive:index})} onMouseOut={() => setChangeImg({isChange: item.imgDisplay,indexActive:index})} />
                                                     ) : ( 
                                                         (item.typeColor.length > 0) ? (<CardImg top width="100%" src={item.typeColor[isClickChange.isIndexActive].imgColor} onMouseOver={() => setChangeImg({isChange: item.imgContinue,indexActive:index})} onMouseOut={() => setChangeImg({isChange: item.imgDisplay,indexActive:index})} />):
                                                        (<CardImg top width="100%" src={item.imgDisplay} onMouseOver={() => setChangeImg({isChange: item.imgContinue,indexActive:index})} onMouseOut={() => setChangeImg({isChange: item.imgDisplay,indexActive:index})} />)
                                                     )
                                                    )

                                                }

                                                <CardBody className="title-price-style"> 
                                                    <CardTitle tag="h6">{item.name}</CardTitle>

                                                    <CardSubtitle tag="h6" className="mb-2 text-muted">{item.price}</CardSubtitle>

                                                    {
                                                        item.typeColor.length !== 0 ? (
                                                        <div className="btn-change-color"> 
                                                            {
                                                                item.typeColor.map((itemColor,index) => {
                                                                    return (
                                                                    <div className="button-group">  
                                                                    {
                                                                        (itemColor.imgColor === item.imgDisplay) ? (<input type="radio" name="radio" id={index} checked/>) : (<input type="radio" name="radio" id={index}/>)
                                                                    }                                                                   
                                                                        <label className={itemColor.color} title={itemColor.color} onClick={() => setClickChange({isClick : isClickChange.isClick, isIndexActive: index})} for={index}></label> 
                                                                    </div>
                                                                    )
                                                                })
                                                            }
                                                        </div>
                                                        )  :  (<div></div>)
                                                    }    
                                                </CardBody>
                                            </Card>
                                        </Col>
                                    );
                                })
                            }
                        </Row>
                    </div>
                    <div className="button-all">
                        <Row>
                            <input type="button" className={slideStyle} value="See All" onMouseOver={() => {setSlide({ isSlide: true})}} onMouseOut={() => {setSlide({ isSlide: false})}}/>
                        </Row>
                    </div>
                </section>
            </Col>
        </Row>

    );
}
