import axios from "axios"

const Menu = {
    listMenuOfMen:{},
    listMenuOfWomen:{}
}

export const SetListMenu = (menu) => ({
    type: "SET",
    payload:menu
})




export const GetListMenu = () => async (dispatch) => {
    const res = await axios.get("/menu")
    dispatch(SetListMenu(res.data))
}


const reducer = (state = Menu ,action) => {
    switch(action.type){
        case "SET":
            console.log(action.payload.listMenuOfMen)
            console.log(action.payload.listMenuOfWomen)
            return{
                ...state,
                listMenuOfMen: action.payload.listMenuOfMen,
                listMenuOfWomen: action.payload.listMenuOfWomen
            }         
        default:
            return state;
    }
}

export default reducer;
