import axios from "axios";


const ListItemBag = {
    items: []
}


export const GetItemBag = (items) => ({
    type: 'GET-BAG',
    payload:items
})


export const GetItemAPIBag = () => async (dispatch) => {
    const replaceListItem = await axios.get("/bag").data
    dispatch(GetItemBag(replaceListItem))
}




const reducer = (state = ListItemBag , action) => {
    console.log(action)
    switch(action.type){
        case 'GET-BAG':
            return{
                ...state,
                items: action.payload
            }
        default:
            return state;
    }
}

export default reducer;
