import './App.css';
import React from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

import HomeScreen from "./screen/HomeScreen"
import ItemDetail from "./screen/ItemDetailScreen"
import ItemBuy from "./screen/ItemBuyScreen"
import Menu from "./menuComponent/menu/MenuComponent"



function App() {

  const [MenuOfWoMen,setListWoment] = useState([]);
  const [MenuOfMen,setListMen] = useState([]);


  useEffect(() => {
    axios.get("http://localhost:3000/menu")
         .then(res => {
           let menu = res.data;
           setListMen(Object.entries(menu.listMenuOfMen));
           setListWoment(Object.entries(menu.listMenuOfWoMen))
         })
         .catch(err => console.log(err))
  },[MenuOfWoMen,MenuOfMen])

  return(
    <div className="p-0 m-0">
        <Router>
            <Menu MenuOfWoMen={MenuOfWoMen} MenuOfMen={MenuOfMen}/>
            <Switch>
                <Route path="/" exact component={HomeScreen} />

                <Route path="/details" component={ItemDetail} />

                <Route path="/buy" component={ItemBuy} />
            </Switch>
        </Router>
    </div>
  );
}

export default App;
