import { connect } from "react-redux"
import ItemComponent from "../pageComponents/item/ItemComponent"
import ItemDetailComponent from "../detailComponents/ItemDetailComponent/ItemDetailComponent"
import { GetItem } from "../redux/items/ItemReducer"

const mapStateToComponent = (store) => ({
    listItemCard: store.listItem.card,
    listItemBag: store.listItem.bag
})

const mapPropsToComponent = (dispatch) => ({
    getItem:(category) => dispatch(GetItem(category))
})

// export default connect(mapPropsToComponent,mapPropsToComponent)(ItemComponent)

export default {
    ListItemContainer: connect(mapStateToComponent,mapPropsToComponent)(ItemComponent),
    ListItemDetial: connect(mapStateToComponent,mapPropsToComponent)(ItemDetailComponent)
}
