import React, { useState,useRef, useEffect } from 'react';
import {
    Row,
    Col
} from 'reactstrap';

import img from "../img/img"

import "./JourneyComponent.css"

import ReactDOM from "react-dom"

export default function JourneyComponent(){

    const [changeContent,setContent] = useState(0)
    const wrapperDescripton = useRef();

    // useEffect(() => {
    //     document.addEventListener("click",handleClick)
    // })


    // function handleClick(e){
    //     const { target } = e;
    //     console.log(target);
    //     console.log(wrapperDescripton.current)
    //     if (!wrapperDescripton.current.contains(target)) {
    //         wrapperDescripton.current.focus();
    //     }
    // }

    const listDescription = [{
        title:"The early days",
        img: img[17],
        description:["The idea and ambition to establish the name Leonardo - a brand developed by Vietnamese people began to be formed by a group of young enthusiastic people. And does anyone expect, it is a restart for the strong development of Leonardo in the future."
        ]
    },
    {
        title:"First Success",
        img: img[18],
        description:["The fashion lines of Leonardo approached and quickly captured the hearts of many Vietnamese consumers, thanks to the variety of models and good product quality."
        ]
    },
    {
        title:"Step Change",
        img: img[19],
        description:["The first two stores respectively opened in Ho Chi Minh (No. 284 Pastuer) and Hanoi (No. 8 Tran Quoc Toan). This is an important premise for future strong developments."
        ]
    },
    {
        title:"A New Chapter",
        img: img[20],
        description:["Following the success, the 3rd (348C CMT8) and 4th (244 Tran Hung Dao) stores officially came into operation, marking a breakthrough of Leonardo, opening a new promising stage. ."
        ]
    },
    {
        title:"The Present",
        img: img[21],
        description:["We believe that with his efforts and enthusiasm, Leonardo will certainly go far in the future.",
        "And for that, certainly indispensable for all of you - one of the prerequisites for our success.",
        "Thank you for always accompanying Leonardo and let's continue to write this beautiful story together."
        ]
    }
    ]

    let textDescription = "description-journey animation-description"

    function animationDescription(e,index){
        setContent(index);
        if(textDescription == "description-journey animation-description"){
            textDescription = textDescription.split(" ")[0];        
        }else if(textDescription == "description-journey"){
            textDescription += " animation-description";
        }

    }





    return(

                <div>
                    <div className="wrapper-journey">
                        <div className="container-journey">
                            <Row className="pt-4 pb-4">
                                <Col sm="12" xs="12" lg="6" xl="6" className="img-journey"> 
                                    <img src={listDescription[changeContent].img} alt="Best Item" title="Best Item" />  
                                </Col>

                                <Col sm="12" xs="12" lg="6" xl="6" className={textDescription} id={"changeContent" + changeContent}>
                                            <h4>{listDescription[changeContent].title}</h4>
                                            {
                                                listDescription[changeContent].description.map(item => {
                                                    return(
                                                        <p>{item}</p>
                                                    )
                                                })
                                            }
                                </Col>
                            </Row>
                            <Row className="years-journey">
                                <div ref={wrapperDescripton}>
                                        <button type="button" title="2014" alt="2014"  onClick={() => {animationDescription(null,0)}}>2014</button>
                                        <button type="button" title="2015 - 2016" alt="2015 - 2016" onClick={() => {animationDescription(null,1)}}>2015 - 2016</button>
                                        <button type="button" title="2017" alt="2017" onClick={() => {animationDescription(null,2)}}>2017</button>
                                        <button type="button" title="2018" alt="2018" onClick={() => {animationDescription(null,3)}}>2018</button>
                                        <button type="button" title="2019" alt="2019" onClick={() => {animationDescription(null,4)}}>2019</button>
                                </div>
                            </Row>
                        </div>
                    </div>
                </div>
    )
}
