import "./MenuComponent.css";
import { Row,Col,
  Navbar,
  Nav,
  NavItem,
  NavLink,
  TabContent, 
  TabPane
  } from "reactstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from "react"; 



export default function MenuComponent(){
  var listMenuOfMen = {
    name: ["wallet","shoes","bags","accessories"], 
    activeTab:"1"
  }

  var listMenuOfWoMen = {
    name: ["shirt","trousers","bags","accessories"], 
    activeTab:"2"
  }


  const [activeTab, setActiveTab] = useState({
    listMenu: [],
    activeTab:""
  });

  const toggle = tab => {
    if(activeTab !== tab){
      if(tab === "1"){
        setActiveTab(listMenuOfMen)
      }
      if(tab === "2"){
        setActiveTab(listMenuOfWoMen)
      }
    } else if(tab === ""){
      return;
    }
  }
   return(
     <Row className="menu">
       <Col lg="12">
         <Row className="top-menu">
           <Col lg="11" sm="6" xs="6">
              <h3 class="logo-nav">LEONARDO</h3>
           </Col>
           <Col lg="1" sm="6" xs="6">
           <div className="sidebar-menu">
              <input type="button" class="btn btn-default" value="Searching" />
              <input type="button" class="btn btn-default" value="Cart" />
            </div>
           </Col>      
        </Row>
       </Col>
       <Col lg="12">
              <Nav className="mr-auto" className="menu-bar">
                <NavItem className="animation-link" >
                  <NavLink href="/components/">NEW ARRIVALS</NavLink>
                </NavItem>
                <NavItem className="animation-link" onMouseOut={() => { toggle(""); }} onMouseOver={() => { toggle('1'); }}>
                  <NavLink href="/components/">MEN</NavLink>
                </NavItem>
                <NavItem className="animation-link" onMouseOut={() => { toggle(""); }} onMouseOver={() => { toggle('2'); }}>
                  <NavLink href="#">WOMENT</NavLink>
                </NavItem>
                <NavItem className="animation-link">
                  <NavLink href="#">BLOG</NavLink>
                </NavItem>        
              </Nav>
       </Col>
       <Col>
        <TabContent activeTab={activeTab.activeTab}>
          <TabPane tabId="1">
            <Row>
              <Col sm="12">
                <h4>Tab 1 Contents</h4>
              </Col>
            </Row>
          </TabPane>
          <TabPane tabId="2">
            <Row>
              <Col sm="12">
                <h4>Tab 2 Contents</h4>
              </Col>
            </Row>
          </TabPane>
          </TabContent>

       </Col>
     </Row>
   );
}
