import axios from "axios"

const Menu = {
    listMenuOfMen:{},
    listMenuOfWoMen:{}
}

export const SetListMenu = (menu) => ({
    type: "SET",
    payload:menu
})

export const SetFailed = (err) => ({
    type:"FAILED",
    payload:err
})




export const GetListMenu = () => async (dispatch) => {
    await axios.get("/menu")
         .then(res => {
            dispatch(SetListMenu(res.data))
         })
         .catch(err => {
            dispatch(SetFailed(err))
         })

}


const reducer = (state = Menu ,action) => {
    switch(action.type){
        case "SET":
            return{
                ...state,
                listMenuOfMen: action.payload.listMenuOfMen,
                listMenuOfWoMen: action.payload.listMenuOfWoMen
            }
        case "FAILED":
            return action.payload         
        default:
            return state;
    }
}

export default reducer;
