import axios from "axios";

const ListItemCard = {
    items: []
}


export const GetItemCard = (items) => ({
    type: 'GET-CARD',
    payload:items
})


export const GetItemAPICard = () => async (dispatch) => {
    const listItem = await axios.get("/card")
    dispatch(GetItemCard(listItem.data))
}





const reducer = (state = ListItemCard , action) => {
    console.log(action)
    switch(action.type){
        case 'GET-CARD':
            return{
                ...state,
                items: action.payload
            }
        default:
            return state;
    }
}

export default reducer;
