import axios from "axios";

const ListItem = {
        card:[],
        bag:[]
}


export const GetItem = (category) => {
    return dispatch => {
        axios.get(`/${category}`)
        .then((res) => {
            if(category == "Wallet"){
                dispatch({type : 'GET-CARD', payload : res.data});
            }else if(category == "Bags"){
                dispatch({type : 'GET-BAG', payload : res.data});
            }
        })
        .catch((err) => {
            dispatch({type : 'FAILED', payload : err});
        });

    }
}


const reducer = (state = ListItem , action) => {
    switch(action.type){
        case 'GET-CARD':
                return{
                    ...state,
                    card: action.payload
                }
        case 'GET-BAG':
                return{
                    ...state,
                    bag: action.payload
                }
        case 'FAILED':
            console.log(action.payload)
        default:
            return state;
    }
}

export default reducer;
