import "./MenuComponent.css";
import { Row,Col,
  Navbar,
  Collapse,
  NavbarToggler,
  Nav,
  NavItem,
  NavLink
  } from "reactstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from "react"; 



export default function MenuComponent(){
  var listMenuOfMen = {
    Wallet: ["Normal Wallet","Long Wallet","Mini Wallet","Card Wallet","See All"],
    Shoes:  ["Vans","Balengacia","Nike","Adidas", "See All"],
    Bags:  ["Fjallraven Kanken","Crumpler","Jack Wolfskin","The North Face", "See All"],
    Accessories:  ["Case","Charger Cord","Headphone","Micro", "See All"]
  }

  var listMenuOfWoMen = {
    Shirt: ["Normal Shirt","Long Shirt","Short Shirt","Skirt","See All"],
    Trousers:  ["Normal Trousers","Long Trousers","Nike","Adidas", "See All"],
    Bags:  ["Fjallraven Kanken","Crumpler","Jack Wolfskin","The North Face", "See All"],
    Accessories:  ["Case","Charger Cord","Headphone","Micro", "See All"]
  }

  const [isOpen, setIsOpen] = useState(false);

  const toggle1 = () => setIsOpen(!isOpen);


  const [dropdownOpen, setOpen] = useState({
    isDropdown:false,
    isDropdown1:false
  });

  var dropdownList,dropdownList1 ;
  if(dropdownOpen.isDropdown){
    dropdownList = "dropdown-item" ;
  }else{
    dropdownList = "dropdown-hidden";
  }

  if(dropdownOpen.isDropdown1){
    dropdownList1 = "dropdown-item" ;
  }else{
    dropdownList1 = "dropdown-hidden";
  }

  const [clickOpen, setClickOpen] = useState({
    isClick : false
  })


  function openInput(e){
    e.preventDefault();
    setClickOpen({isClick: !clickOpen.isClick});
  } 

  const display = clickOpen.isClick ? "show-input":"hidden-input";



   return(
     <Row>
       <Col lg="12" sm="10" className="p-0">
         <Row className="top-menu">
           <Col lg="11" sm="6" xs="6">
              <h3 class="logo-nav">LEONARDO</h3>
           </Col>
           <Col lg="1" sm="6" xs="6">
           <div className="sidebar-menu">
              <input type="button" class="btn btn-default" value="Searching" onClick={openInput}/>
              <input type="button" class="btn btn-default" value="Cart" />
            </div>
           </Col>      
        </Row>
       </Col>
       <Col lg="12" sm="2" className="p-0">
       <Navbar light expand="md" className="m-0 p-0 menu-dropdown menu">
       <NavbarToggler onClick={toggle1} />
          <Collapse isOpen={isOpen} navbar>
              <Nav className="mr-auto" className="menu-bar">
                  <div className="p-0 m-0" >
                    <NavItem className="animation-link" >
                      <NavLink href="/components/">NEW ARRIVALS</NavLink>
                    </NavItem>
                  </div>

                  <div className="p-0 m-0" onMouseOut={() => { setOpen({ isDropdown:false }); }} onMouseOver={() => { setOpen({isDropdown:true }); }} >
                    <NavItem className="animation-link" > 
                            <NavLink href="/components/">MEN</NavLink> 
                    </NavItem>

                  </div>
                  <div className={dropdownList}>
                              <div className="item-menu">
                                <ul>
                                  <li>{Object.keys(listMenuOfMen)[0]}</li>
                                  {
                                    listMenuOfMen.Wallet.map(item => {
                                      return <li>{item}</li>;
                                    })
                                  }
                                </ul>
                                <ul>
                                <li>{Object.keys(listMenuOfMen)[1]}</li>
                                  {
                                    listMenuOfMen.Shoes.map(item => {
                                      return <li>{item}</li>;
                                    })
                                  }
                                </ul>
                                <ul>
                                <li>{Object.keys(listMenuOfMen)[2]}</li>
                                  {
                                    listMenuOfMen.Bags.map(item => {
                                      return <li>{item}</li>;
                                    })
                                  }
                                </ul>
                                <ul>
                                <li>{Object.keys(listMenuOfMen)[3]}</li>
                                  {
                                    listMenuOfMen.Accessories.map(item => {
                                      return <li>{item}</li>;
                                    })
                                  }
                                </ul>
                              </div>
                            </div>




                <div className="p-0 m-0" >
                  <NavItem className="animation-link" onMouseOut={() => { setOpen({ isDropdown1:false }); }} onMouseOver={() => { setOpen({ isDropdown1:true }); }}> 
                          <NavLink href="/components/">WOMEN</NavLink> 
                          <div className={dropdownList1}>
                      <div className="item-menu">
                          <ul>
                            {
                              listMenuOfWoMen.Shirt.map(item => {
                                return <li>{item}</li>;
                              })
                            }
                          </ul>
                          <ul>
                            {
                              listMenuOfWoMen.Trousers.map(item => {
                                return <li>{item}</li>;
                              })
                            }
                          </ul>
                          <ul>
                            {
                              listMenuOfWoMen.Bags.map(item => {
                                return <li>{item}</li>;
                              })
                            }
                          </ul>
                          <ul>
                            {
                              listMenuOfWoMen.Accessories.map(item => {
                                return <li>{item}</li>;
                              })
                            }
                          </ul>
                        </div>
                      </div>
                  </NavItem>  
                </div>

                <div className="p-0 m-0" >
                  <NavItem className="animation-link">
                    <NavLink href="#">BLOG</NavLink>
                  </NavItem> 
                </div>       
              </Nav>
          </Collapse>
          </Navbar>
       </Col>
       <Col className="p-0">
        <div class={display}>
              <input type="text" className="input-text" placeholder="Searching..." />
        </div>
       </Col>
     </Row>

   );
}
