import axios from "axios";

const Listitem = {
    items: [],
    replaceItems: []
}


export const GetItemCard = (items) => ({
    type: 'GET-CARD',
    payload:items
})

export const GetItemBag = (items) => ({
    type: 'GET-BAG',
    payload:items
})

export const GetItemAPICard = () => async (dispatch) => {
    const listItem = await axios.get("/card").data
    dispatch(GetItemCard(listItem))
}

export const GetItemAPIBag = () => async (dispatch) => {
    const replaceListItem = await axios.get("/bag").data
    dispatch(GetItemBag(replaceListItem))
}




const reducer = (state = Listitem , action) => {
    switch(action.type){
        case 'GET-CART':
            return{
                ...state,
                items: action.payload
            }
        case 'GET-BAG':
            return{
                ...state,
                replaceItems: action.payload
            }
        default:
            return state;
    }
}

export default reducer;
