import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import ItemBuy from "../buyComponents/itemBuy/ItemBuy"
import ItemDetail from "../screen/ItemDetailScreen"
import { useParams } from 'react-router';
import axios from "axios"


const ItemBuyScreen = () => {

    let { anotherName } = useParams();

    const [ itemBuyDetail , setItemBuy] = useState({})
    if(anotherName !== null){
        axios.get(`/bag?anotherName=${anotherName}`)
             .then(res => {
                setItemBuy(res.data)
             })
             .catch(err => console.log(err))
    } 

    return(
        <div>
            {
                anotherName !== null ? (<ItemBuy />) : (<ItemDetail />)
            }

        </div>
    )
}

export default ItemBuyScreen;
