import axios from "axios";

const ListItem = {
    items:{
        card:[],
        bag:[]
    }
}


export const GetItem = (category) => ({
    type: `GET-${category}`
})

const fetchData = async () => {
    const res = await axios.get("/card")
    return res.data
}





const reducer = (state = ListItem , action) => {

    switch(action.type){
        case 'GET-CARD':
            const resCard = axios.get("/card")
                return{
                    ...state,
                    items:{
                        ...state.items,
                        card: resCard.data 
                    }
                }
        case 'GET-BAG':
            const resBag = axios.get("/bag")
                return{
                    ...state,
                    items:{
                        ...state.items,
                        card: resBag.data 
                    }
                }
        default:
            return state;
    }
}

export default reducer;
