import axios from "axios";

const ListItem = {
    items:{
        card:[],
        bag:[]
    }
}


export const GetItem = (category) => ({
    type: 'GET',
    payload:category
})

const fetchData = async () => {
    const res = axios.get("/card")
    return res.data
}





const reducer = (state = ListItem , action) => {
    switch(action.type){
        case 'GET':
            const category = action.payload
            const listData = fetchData
            if(category == "card"){
                return{
                    ...state,
                    items:{
                        ...state.items,
                        card: listData.data 
                    }
                }
            }else if(category == "bag"){
                return{
                    ...state,
                    items:{
                        ...state.items,
                        bag: listData.data 
                    }
                }
            }

        default:
            return state;
    }
}

export default reducer;
