import axios from "axios";


const ListItemBag = {
    items: []
}


export const GetItemBag = (items) => ({
    type: 'GET-BAG',
    payload:items
})


export const GetItemAPIBag = () => async (dispatch) => {
    const replaceListItem = await axios.get("/bag")
    dispatch(GetItemBag(replaceListItem.data))
}




const reducer = (state = ListItemBag , action) => {
    switch(action.type){
        case 'GET-BAG':
            return{
                ...state,
                items: action.payload
            }
        default:
            return state;
    }
}

export default reducer;
