import "./MenuComponent.css";
import { Row,Col,
  Navbar,
  Collapse,
  NavbarToggler,
  Nav,
  NavItem,
  NavLink,
  TabContent, 
  TabPane
  } from "reactstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from "react"; 



export default function MenuComponent(){
  var listMenuOfMen = {
    listMenu: ["wallet","shoes","bags","accessories"], 
    activeTab:"1"
  }

  var listMenuOfWoMen = {
    listMenu: ["shirt","trousers","bags","accessories"], 
    activeTab:"2"
  }

  var search = {
      activeTab:"3"
  }


  const [activeTab, setActiveTab] = useState({
    listMenu: [],
    activeTab:""
  });

  const toggle = tab => {
    if(activeTab !== tab){
      if(tab === "1"){
        setActiveTab(listMenuOfMen)
      }
      if(tab === "2"){
        setActiveTab(listMenuOfWoMen)
      }
      if(tab === "3"){
        setActiveTab(search)
      }
      if(tab === ""){
        setActiveTab(() => {
          const resetTab =  {
            listMenu: [],
            activeTab: ""
          }
          return resetTab;

        })
      }
    } 
  }

  const [isOpen, setIsOpen] = useState(false);

  const toggle1 = () => setIsOpen(!isOpen);
   return(
     <Row className="menu">
       <Col lg="12" sm="10">
         <Row className="top-menu">
           <Col lg="11" sm="6" xs="6">
              <h3 class="logo-nav">LEONARDO</h3>
           </Col>
           <Col lg="1" sm="6" xs="6">
           <div className="sidebar-menu">
              <input type="button" class="btn btn-default" value="Searching" onClick={() => { toggle('3'); }}/>
              <input type="button" class="btn btn-default" value="Cart" />
            </div>
           </Col>      
        </Row>
       </Col>
       <Col lg="12" sm="2">
       <Navbar light expand="md" className="m-0 p-0">
       <NavbarToggler onClick={toggle1} />
          <Collapse isOpen={isOpen} navbar>
              <Nav className="mr-auto" className="menu-bar">
                <NavItem className="animation-link" >
                  <NavLink href="/components/">NEW ARRIVALS</NavLink>
                </NavItem>
                <NavItem className="animation-link" onMouseOut={() => { toggle(""); }} onMouseOver={() => { toggle('1'); }}>
                  <NavLink href="/components/">MEN</NavLink>
                </NavItem>
                <NavItem className="animation-link" onMouseOut={() => { toggle(""); }} onMouseOver={() => { toggle('2'); }}>
                  <NavLink href="#">WOMENT</NavLink>
                </NavItem>
                <NavItem className="animation-link">
                  <NavLink href="#">BLOG</NavLink>
                </NavItem>        
              </Nav>
          </Collapse>
          </Navbar>
       </Col>
       <Col>
        <TabContent activeTab={activeTab.activeTab}>
          <TabPane tabId="1">
            <Row>
              <Col sm="12">
                <ul className="listMenu">
                  {
                    activeTab.listMenu.map((item) => { 
                        return <li>{item}</li>
                    })
                  }
                </ul>
              </Col>
            </Row>
          </TabPane>
          <TabPane tabId="2">
            <Row>
              <Col sm="12">
                <ul className="listMenu">
                  {
                    activeTab.listMenu.map((item) => { 
                        return <li key={1}>{item}</li>
                    })
                  }
                </ul>
              </Col>
            </Row>
          </TabPane>
          <TabPane tabId="3">
            <Row>
              <Col sm="12">
                <input type="text" placeholder="Enter Items...."/>
              </Col>
            </Row>
          </TabPane>
          </TabContent>

       </Col>
     </Row>
   );
}
