import axios from "axios"

const Menu = {
    listMenuOfMen:[],
    listMenuOfWomen:[]
}

export const SetListMenu = (listMenu) => ({
    type: "SET",
    payload:listMenu
})

const errorGetList = (err) => ({
    type:"FAILED",
    error: err
})


export const GetListMenu = () => (dispatch) => {
    axios.get("/menu").then(res => {
        dispatch(SetListMenu(res.data))
    }).catch(err => {
        dispatch(errorGetList(err))
    })

}


const reducer = (state = Menu ,action) => {
    console.log(action)
    switch(action.type){
        case "SET":
            return{
                ...state,
                listMenuOfMen: action.payload.listMenuOfMen,
                listMenuOfWomen: action.payload.listMenuOfWomen
            }   
        case "FAILED":
            return action.error;    
        default:
            return state;
    }
}

export default reducer;
