import axios from "axios";

const ListItem = {
        card:[],
        bag:[]
}


export const GetItem = (category) => {
    const items = fetchListItem(category);
    return{
        type: `GET-${category}`,
        payload: items
    }
}

const fetchListItem = (category) => {
    let result = {
      category
    };

    axios.get(`/${category}`)
    .then(res => {
        result.category = res.data;
        return result;
    })
    .catch(err => console.log(err));

    return result;
}





const reducer = (state = ListItem , action) => {
    switch(action.type){
        case 'GET-CARD':
                return{
                    ...state,
                    card: action.payload
                }
        case 'GET-BAG':
                return{
                    ...state,
                    bag: action.payload
                }
        default:
            return state;
    }
}

export default reducer;
