import React, { useState } from 'react';
import {
    Row,
    Col,
  Carousel,
  CarouselItem,
  CarouselIndicators,
  CarouselCaption
} from 'reactstrap';

import "./BannerComponent.css"

const items = [
  {
    src: 'https://via.placeholder.com/2000x600',
    altText: 'Slide 1',
    caption: 'Slide 1'
  },
  {
    src: 'https://via.placeholder.com/2000x600',
    altText: 'Slide 2',
    caption: 'Slide 2'
  },
  {
    src: 'https://via.placeholder.com/2000x600',
    altText: 'Slide 3',
    caption: 'Slide 3'
  }
];

const BannerComponent = (props) => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [animating, setAnimating] = useState(false);

  const next = () => {
    if (animating) return;
    const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
    setActiveIndex(nextIndex);
  }

  const previous = () => {
    if (animating) return;
    const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  }

  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  }

  const button = () => {
      return(
        <button className="btn btn-outline-default">Click Me!</button>
      );
  }

  const slides = items.map((item) => {
    return (
      <CarouselItem onExiting={() => setAnimating(true)} onExited={() => setAnimating(false)} key={item.src}>
        <img src={item[0].src} alt={item[0].altText} />
        <CarouselCaption captionText={item[0].caption} captionHeader={item[0].caption}>
          <button className="btn btn-default">Oke Oke</button>
        </CarouselCaption>
      </CarouselItem>
    );
  });

  return (
      <Row>
        <Col className="p-0">
            <Carousel activeIndex={activeIndex} next={next} previous={previous}>
                <CarouselIndicators items={items} activeIndex={activeIndex} onClickHandler={goToIndex} className="indicator-decoration"/>

                <CarouselItem onExiting={() => setAnimating(true)} onExited={() => setAnimating(false)} key={items.src}>
                  <img src={items[0].src} alt={items[0].altText} />
                  <CarouselCaption captionText={items[0].caption} captionHeader={items[0].caption}>
                    <button className="btn btn-default">Oke Oke</button>
                  </CarouselCaption>
                </CarouselItem>
                <CarouselItem onExiting={() => setAnimating(true)} onExited={() => setAnimating(false)} key={items.src}>
                  <img src={items[1].src} alt={items[1].altText} />
                  <CarouselCaption captionText={items[1].caption} captionHeader={items[1].caption}>
                    <button className="btn btn-default">Oke Oke</button>
                  </CarouselCaption>
                </CarouselItem>
                <CarouselItem onExiting={() => setAnimating(true)} onExited={() => setAnimating(false)} key={items.src}>
                  <img src={items[2].src} alt={items[2].altText} />
                  <CarouselCaption captionText={items[2].caption} captionHeader={items[2].caption}>
                    <button className="btn btn-default">Oke Oke</button>
                  </CarouselCaption>
                </CarouselItem>


            </Carousel>
        </Col>
    </Row>
  );
}

export default BannerComponent;
