import "./MenuComponent.css";
import { Row,Col,
  Navbar,
  Collapse,
  NavbarToggler,
  Nav,
  NavItem,
  NavLink
  } from "reactstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import React, { useEffect, useState } from "react"; 

import axios from "axios"



export default function MenuComponent(){


  useEffect(() => {
    axios.get("http://localhost:3000/menu")
         .then(res => {
           const menu = res.data;
           setListMen(Object.entries(menu.listMenuOfMen));
           setListWoment(Object.entries(menu.listMenuOfWoMen))
         })
         .catch(err => console.log(err))
  },[listMenuOfMen,listMenuOfWoMen])

  const [listMenuOfWoMen,setListWoment] = useState([]);
  const [listMenuOfMen,setListMen] = useState([]);

  const [isOpen, setIsOpen] = useState(false);

  const toggle1 = () => setIsOpen(!isOpen);


  const [dropdownOpen, setOpen] = useState({
    isDropdown:false,
    isDropdown1:false
  });


  const dropdownList1 = dropdownOpen.isDropdown1 ? "dropdown-item":"dropdown-hidden";

  const dropdownList = dropdownOpen.isDropdown ? "dropdown-item":"dropdown-hidden";

  const [clickOpen, setClickOpen] = useState({
    isClick : false
  })


  function openInput(e){
    e.preventDefault();
    setClickOpen({isClick: !clickOpen.isClick});
  } 

  const display = clickOpen.isClick ? "show-input":"hidden-input";

   return(
     <Row>
       <Col lg="12" sm="10" className="p-0">
         <Row>
          <div className="top-menu">
                <Col lg="6" sm="7" xs="6" className="p-0">
                  <h3 class="logo-nav"><Link to="/">LEONARDO</Link></h3>
                </Col>      
                <Col lg="6" sm="5" xs="6" className="p-0">
                  <div className="sidebar-menu">
                    <input type="button" class="btn btn-default" value="Searching" onClick={openInput}/>
                    <input type="button" class="btn btn-default" value="Cart" />
                  </div>
                </Col>
          </div>      
        </Row>
       </Col>
       <Col lg="12" sm="2" className="p-0 m-0">
       <Navbar light expand="md" className="m-0 p-0 menu-dropdown menu">
       <NavbarToggler onClick={toggle1} />
          <Collapse isOpen={isOpen} navbar className="p-0">
              <Nav className="p-0" className="menu-bar">
                  <div className="p-0 m-0" >
                    <NavItem className="animation-link" >
                      <NavLink href="/details">NEW ARRIVALS</NavLink>
                    </NavItem>
                  </div>

                  <div className="p-0 m-0" onMouseOut={() => { setOpen({ isDropdown:false }); }} onMouseOver={() => { setOpen({isDropdown:true }); }} >
                    <NavItem className="animation-link" > 
                            <NavLink href="/details">MEN</NavLink> 
                    </NavItem>
                    <div className={dropdownList}>
                              <div className="item-menu">
                                <ul className="list-item">
                                  <li className="title-list-item">{Object.keys(listMenuOfMen)[0]}</li>
                                  {
                                    listMenuOfMen.Wallet.map((item,index) => {
                                      if(index == 0) {
                                        return <Link to="/details"><li>{item}</li></Link>;
                                      }
                                      return  <li>{item}</li>;
                                    })
                                  }
                                </ul>
                                <ul className="list-item">
                                <li className="title-list-item">{Object.keys(listMenuOfMen)[1]}</li>
                                  {
                                    listMenuOfMen.Shoes.map(item => {
                                      return <li>{item}</li>;
                                    })
                                  }
                                </ul>
                                <ul className="list-item">
                                <li className="title-list-item">{Object.keys(listMenuOfMen)[2]}</li>
                                  {
                                    listMenuOfMen.Bags.map(item => {
                                      return <li>{item}</li>;
                                    })
                                  }
                                </ul>
                                <ul className="list-item">
                                <li className="title-list-item">{Object.keys(listMenuOfMen)[3]}</li>
                                  {
                                    listMenuOfMen.Accessories.map(item => {
                                      return <li>{item}</li>;
                                    })
                                  }
                                </ul>
                              </div>
                            </div>

                  </div>




                <div className="p-0 m-0" onMouseOut={() => { setOpen({ isDropdown1:false }); }} onMouseOver={() => { setOpen({ isDropdown1:true }); }}>
                  <NavItem className="animation-link"> 
                          <NavLink href="/details">WOMEN</NavLink>                           
                  </NavItem>  
                  <div className={dropdownList1}>
                            <div className="item-menu">
                                <ul className="list-item">
                                  {
                                    listMenuOfWoMen.Shirt.map(item => {
                                      return <li>{item}</li>;
                                    })
                                  }
                                </ul>
                                <ul className="list-item">
                                  {
                                    listMenuOfWoMen.Trousers.map(item => {
                                      return <li>{item}</li>;
                                    })
                                  }
                                </ul>
                                <ul className="list-item">
                                  {
                                    listMenuOfWoMen.Bags.map(item => {
                                      return <li>{item}</li>;
                                    })
                                  }
                                </ul>
                                <ul className="list-item">
                                  {
                                    listMenuOfWoMen.Accessories.map(item => {
                                      return <li>{item}</li>;
                                    })
                                  }
                                </ul>
                              </div>
                          </div>
                </div>

                <div className="p-0 m-0" >
                  <NavItem className="animation-link">
                    <NavLink href="#">BLOG</NavLink>
                  </NavItem> 
                </div>       
              </Nav>
          </Collapse>
          </Navbar>
       </Col>
       <Col className="p-0">
        <div class={display}>
              <input type="text" className="input-text" placeholder="Searching..." />
        </div>
       </Col>

     </Row>

   );
}
