import { connect } from "react-redux"
import ItemComponent from "../pageComponents/item/ItemComponent"
import { GetItem,GetItemAPI } from "../redux/items/ItemCardReducer"
import { GetItemAPIBag,GetItemBag } from "../redux/items/ItemBagReducer"

const mapStateToComponent = (store) => ({
    listItemCard: store.listItem.items.card,
    listItemBag: store.listItem.items.bag
})

const mapPropsToComponent = (dispatch) => ({
    getItem:(category) => dispatch(GetItem(category)),
    getItemAPI: (category) => dispatch(GetItemAPI(category))
})

export default connect(mapStateToComponent,mapPropsToComponent)(ItemComponent)
