import React, { useState,useRef, useEffect } from 'react';
import {
    Row,
    Col,
    Collapse, Button
} from 'reactstrap';
import "./ItemBuy.css";
import imgItem from "/home/quocthang123/Desktop/Leonardo/leonardo-app/src/components/img/img"
import "/home/quocthang123/Desktop/Leonardo/leonardo-app/src/components/itembest/ItemBest.css"
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import $ from 'jquery';

export default function ItemBuy(){

    const itemColor = {
        name: "Túi Hộp Oxford",
        price: "320.000₫",
        listColor:[{
            color:"gray",
            imgColor:imgItem[12]
        },
        {
            color:"black",
            imgColor:imgItem[14]
        }]    
    }

    const responsive = {
        superLargeDesktop: {
          // the naming can be any, depends on you.
          breakpoint: { max: 4000, min: 3000 },
          items: 5
        },
        desktop: {
          breakpoint: { max: 3000, min: 1024 },
          items: 5
        },
        tablet: {
          breakpoint: { max: 1024, min: 464 },
          items: 2
        },
        mobile: {
          breakpoint: { max: 464, min: 0 },
          items: 1
        }
      };

    const [changeColorItemBest, setChange] = useState({
        colorChange: itemColor.listColor[0].imgColor,
        isChange:false,
        indexActive: 0
    })

    $(document).ready(function(){
            $(window).on('scroll',function(){

              var link = $('.btn-product-buy a');
              var top = $(window).scrollTop();

              $('.width-product-buy img').each(function(){
                var id = $(this).attr('id');
                var height = $(this).height();
                var offset = $(this).offset().top - 150;
                if(top >= offset && top < offset + height){
                  link.removeClass('active');
                  $('.btn-product-buy').find('[data-scroll="' + id + '"]').addClass('active');
                }
              });

            });
    })


    return(
        <div className="container-product-buy">
            <Row>
                <Col sm="12" lg="6">
                    <div style={{height: "100%", width: "fit-content"}}>
                        <div className="width-product-buy">
                            <img id="img1" src={imgItem[1]} alt="item-1" title="item-1"/>
                            <img id="img2" src={imgItem[2]} alt="item-1" title="item-1"/>
                            <img id="img3" src={imgItem[3]} alt="item-1" title="item-1"/>
                            <img id="img4" src={imgItem[4]} alt="item-1" title="item-1"/>
                            <img id="img5" src={imgItem[5]} alt="item-1" title="item-1"/>
                        </div>
                        <div className="btn-product-buy">
                            <a href="#img1" id="btn-img1" data-scroll="img1"></a>
                            <a href="#img2" id="btn-img2" data-scroll="img2"></a>
                            <a href="#img3" id="btn-img3" data-scroll="img3"></a>
                            <a href="#img4" id="btn-img4" data-scroll="img4"></a>
                            <a href="#img5" id="btn-img5" data-scroll="img5"></a>
                        </div>                      
                    </div>
                    <div className="wrapper-product-aside">
                            <div className="product-aside">
                                <button>
                                    Warranty Policy
                                    <span className="icon-collapse"></span>
                                </button>
                                <button>
                                    Form of Transportation
                                    <span className="icon-collapse"></span>
                                </button>
                            </div>
                        </div>

                </Col>
                <Col sm="12" lg="6">
                    <div className="wrapper-detail-product">
                        <div className="position-product-fixed">
                            <div className="detail-product">
                                <ul className="list-info-item" style={{position: "sticky",top: "0"}}>
                                    <li>
                                        <h3 className="mt-4 name-best-item">Card Feasty Wallet</h3>
                                    </li>
                                    <li>
                                        <p className="price-best-item"><span className="text-muted"><del>260.000₫</del></span><span className="ml-3 text-danger">130.000₫</span></p> 
                                    </li>
                                    <li>
                                        <p className="text-color">
                                            Color: Black
                                        </p>
                                    </li>   
                                    <li className="display-btn-changeColor">
                                        {
                                            itemColor.listColor.map((color,index) => {

                                                return(
                                                    <div className="button-group-item-best">
                                                        {
                                                        (changeColorItemBest.indexActive === index ) ? 
                                                            (
                                                                <input type="radio" id={index + "itemBest"} name="radio" checked autoFocus/> 
                                                            )
                                                            :
                                                            (
                                                                <input type="radio" id={index + "itemBest"} name="radio" />
                                                            ) 
                                                        }
                                                        <label className={color.color} for={index + "itemBest"}></label>
                                                    </div>

                                                )
                                            })
                                        }
                                    </li>     
                                    <li>
                                        <Button className="btn-touch-item b-black" style={{width: "100%"}}>ADD TO CART</Button>
                                    </li>
                                    <li>
                                        <p>With a design that breathes modern fashion, Zumba cross-straps backpack was born. The product is an intersection between backpack and small cross pocket.</p>
                                    </li>
                                    <li>
                                        <ul>
                                            <li>Material: 100% Polyester</li>
                                            <li>Dimensions: 7 x 17 x 27 cm</li>
                                            <li>Be contain Ipad Pro 10.5 inch</li>
                                            <li>1 large compartment, 1 small inner compartment, 2 outer compartments</li>
                                            <li>2 side compartments</li>
                                        </ul>
                                    </li>
                                </ul>
                            </div>
                        </div>
                    </div>
                </Col>
            </Row>
        </div>
    );
}
