import axios from "axios";

const ListItem = {
        card:[],
        bag:[]
}


export const GetItem = (category) => ({
    type: `GET-${category}`
})







const reducer = (state = ListItem , action) => {
    switch(action.type){
        case 'GET-CARD':
            const resCard = axios.get("/card")
                return{
                    ...state,
                    card: resCard.data 
                }
        case 'GET-BAG':
            const resBag = axios.get("/bag")
                return{
                    ...state,
                    card: resBag.data 
                }
        default:
            return state;
    }
}

export default reducer;
