import "./MenuComponent.css";
import { Row,Col,
  Navbar,
  Collapse,
  NavbarToggler,
  Nav,
  NavItem,
  NavLink,
  Fade,
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
  } from "reactstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from "react"; 



export default function MenuComponent(){
  var listMenuOfMen = {
    listMenu: ["wallet","shoes","bags","accessories"]
  }

  var listMenuOfWoMen = {
    listMenu: ["shirt","trousers","bags","accessories"], 
  }


  const [ fadeIn, setFadeIn ] = useState(false);
  const [ listItems, setListItems ] = useState({});  

  const toggle = () => {
    if(toggle === "1"){
      setListItems(listMenuOfMen)
    }else if(toggle === "2"){
      setListItems(listMenuOfWoMen)
    }
    setFadeIn(!fadeIn);
  } 


  const [isOpen, setIsOpen] = useState(false);

  const toggle1 = () => setIsOpen(!isOpen);


  const [dropdownOpen, setOpen] = useState(false);

  const toggle2 = () => setOpen(!dropdownOpen);
   return(
     <Row className="menu">
       <Col lg="12" sm="10">
         <Row className="top-menu">
           <Col lg="11" sm="6" xs="6">
              <h3 class="logo-nav">LEONARDO</h3>
           </Col>
           <Col lg="1" sm="6" xs="6">
           <div className="sidebar-menu">
              <input type="button" class="btn btn-default" value="Searching" onClick={() => { toggle('3'); }}/>
              <input type="button" class="btn btn-default" value="Cart" />
            </div>
           </Col>      
        </Row>
       </Col>
       <Col lg="12" sm="2">
       <Navbar light expand="md" className="m-0 p-0">
       <NavbarToggler onClick={toggle1} />
          <Collapse isOpen={isOpen} navbar>
              <Nav className="mr-auto" className="menu-bar">
                <NavItem className="animation-link" >
                  <NavLink href="/components/">NEW ARRIVALS</NavLink>
                </NavItem>

                  <ButtonDropdown isOpen={dropdownOpen} toggle={toggle2}>
                     <DropdownToggle  className="btn-dropdown">
                        <NavItem className="animation-link is-expanded" onMouseOut={() => { toggle("1"); }} onMouseOver={() => { toggle('1'); }}>
                          <NavLink href="/components/">MEN</NavLink>
                        </NavItem>
                      </DropdownToggle>

                      <DropdownMenu>
                        <DropdownItem header>Header</DropdownItem>
                        <DropdownItem disabled>Action</DropdownItem>
                        <DropdownItem>Another Action</DropdownItem>
                        <DropdownItem divider />
                        <DropdownItem>Another Action</DropdownItem>
                      </DropdownMenu>
                  </ButtonDropdown>

                <NavItem className="animation-link" onMouseOut={() => { toggle("2"); }} onMouseOver={() => { toggle('2'); }}>
                  <NavLink href="#">WOMENT</NavLink>
                </NavItem>
                <NavItem className="animation-link">
                  <NavLink href="#">BLOG</NavLink>
                </NavItem>        
              </Nav>
          </Collapse>
          </Navbar>
       </Col>
     </Row>
   );
}
