import { connect } from "react-redux"
import ItemComponent from "../pageComponents/item/ItemComponent"
import { GetItemAPICard,GetItemCard} from "../redux/items/ItemReducer"
import { GetItemAPIBag,GetItemBag} from "../redux/items/ItemBagReducer"

const mapStateToComponent = (store) => ({
    listItemCard: store.listItem.items,
    listItemBag: store.listItem.items
})

const mapPropsToComponent = (dispatch) => ({
    getItemBag:(items) => dispatch(GetItemBag(items)),
    getItemCard:(items) => dispatch(GetItemCard(items)),
    getListItemCard: () => dispatch(GetItemAPICard()),
    getListItemBag: () => dispatch(GetItemAPIBag())
})

export default connect(mapStateToComponent,mapPropsToComponent)(ItemComponent)
