import axios from "axios"

const Menu = {
    listMenuOfMen:{},
    listMenuOfWoMen:{}
}

export const SetListMenu = (menu) => ({
    type: "SET",
    payload:menu
})




export const GetListMenu = () => (dispatch) => {
    axios.get("/menu")
         .then(res => {
            dispatch(SetListMenu(res.data))
         })
         .catch(err => {
             console.log(err)
         })

}


const reducer = (state = Menu ,action) => {
    switch(action.type){
        case "SET":
            return{
                ...state,
                listMenuOfMen: action.payload.listMenuOfMen,
                listMenuOfWoMen: action.payload.listMenuOfWoMen
            }         
        default:
            return state;
    }
}

export default reducer;
