import axios from "axios"

const Cart = {
    items:[]
}

export const SetListCart = (items) => ({
    type: "SET",
    payload:items
})

export const DeleteItemCart = (id) => ({
    type: "DELETE",
    payload: id
})

const errorGet = (error) => ({
    type:'FAILED',
    error:error
})



export const GetListCart = () => (dispatch) => {
    axios.get("/cart").then(res => {
        SetListCart(res.data)
    }).catch(err => {
        dispatch(errorGet(err))
    })

}


const reducer = (state = Cart ,action) => {
    switch(action.type){
        case "SET":
            return{
                ...state,
                items: action.payload
            }    
        case "DELETE":
            axios.delete(`/cart/${action.payload}/`)  
            return state;   
        case "FAILED":
            return action.error  
        default:
            return state;
    }
}

export default reducer;
