import axios from "axios";

const ListItem = {
    items:{
        card:[],
        bag:[]
    }
}


export const GetItem = (category) => ({
    type: 'GET',
    payload:category
})

export const GetItemAPI = async (dispatch) =>{

}






const reducer = (state = ListItem , action) => {
    switch(action.type){
        case 'GET':
            const category = action.payload
            if(category === "card"){
                const listData = axios.get("/card")
                return{
                    ...state,
                    items:{
                        ...state.items,
                        card: listData.data 
                    }
                }
            }else if(category === "bag"){
                const listData = axios.get("/bag")
                return{
                    ...state,
                    items:{
                        ...state.items,
                        bag: listData.data 
                    }
                }
            }

        default:
            return state;
    }
}

export default reducer;
